// Get incoming data (first item)
const data = items[0].json;

// If wrapped in an array, unwrap it
const input = Array.isArray(data) ? data[0] : data;

let output = [
  {
    content: input["Document data"], // main text for embedding
    metadata: {
      document_name: input["Document name"],
      source_id: input[" source"], // note: there is a space before "source" in your key
    }
  }
];

return output.map(o => ({ json: o }));

const chunkSize = 1000;
const chunkOverlap = 100;
const imageBaseUrl = "https://rmlkaysgqjrtjedpopha.supabase.co/storage/v1/object/public/chatbot/newextracted_imagesnew/";

let newItems = [];

for (const item of items) {
  const doc = item || {};
  const sourceId = doc.source || "unknown";
  let text = doc["Document data"] || "";

  if (!text.trim()) continue; // skip empty docs

  // Split by page markers: "Page X"
  const pageSplits = text.split(/Page\s+(\d+)/);

  for (let i = 1; i < pageSplits.length; i += 2) {
    const pageNumber = parseInt(pageSplits[i], 10);
    let pageText = pageSplits[i + 1] || "";

    if (!pageText.trim()) continue;

    // Replace inline image refs with markdown links
    pageText = pageText.replace(/Image\s+\[([^\]]+)\]/g, (match, p1) => {
      return `![${p1}](${imageBaseUrl}${sourceId}_${p1})`;
    });

    // Split text into paragraphs/sentences
    const parts = pageText.split(/(\n+|\.\s+)/);
    let currentChunk = "";
    let chunkStart = 0;
    let chunkIndex = 0;

    for (const part of parts) {
      if (currentChunk.length + part.length <= chunkSize) {
        currentChunk += part;
      } else {
        // Push full chunk with metadata
        const chunkEnd = chunkStart + currentChunk.length;
        newItems.push({
          json: {
            content: currentChunk.trim(),
            metadata: {
              source: sourceId,
              page: pageNumber,
              loc: { start: chunkStart, end: chunkEnd },
              chunk_id: `${sourceId}_p${pageNumber}_c${chunkIndex}`
            }
          }
        });

        // Overlap logic
        currentChunk = currentChunk.slice(-chunkOverlap) + part;
        chunkStart = chunkEnd - chunkOverlap;
        if (chunkStart < 0) chunkStart = 0;
        chunkIndex++;
      }
    }

    // Push last chunk on page
    if (currentChunk.trim()) {
      const chunkEnd = chunkStart + currentChunk.length;
      newItems.push({
        json: {
          content: currentChunk.trim(),
          metadata: {
            source: sourceId,
            page: pageNumber,
            loc: { start: chunkStart, end: chunkEnd },
            chunk_id: `${sourceId}_p${pageNumber}_c${chunkIndex}`
          }
        }
      });
    }
  }
}

return newItems;

const chunkSize = 1000; // characters
const chunkOverlap = 100; // characters

let newItems = [];

for (const item of items) {
    let text = item.json.content; // <-- match your field name
    if (!text || typeof text !== "string") continue; // skip if no text

    for (let i = 0; i < text.length; i += chunkSize - chunkOverlap) {
        let chunk = text.slice(i, i + chunkSize);
        newItems.push({
            json: {
                content: chunk,
                metadata: item.json.metadata
            }
        });
    }
}

return newItems;

