// n8n Function node
// Keeps image array from input, adds embedding_text per page

const pages = items[0].json.pages;
let output = [];

for (const page of pages) {
  let text = `Page ${page.index}\n\n`;

  // OCR markdown text, but strip image tags
  const cleanMarkdown = page.markdown.replace(/!\[.*?\]\(.*?\)/g, "").trim();
  if (cleanMarkdown) {
    text += cleanMarkdown + "\n\n";
  }

  // Add annotations for each image
  for (const img of page.images) {
    if (img.image_annotation) {
      const ann = JSON.parse(img.image_annotation);
      text += `Image [${img.id}]\n`;
      text += `Type: ${ann.image_type}\n`;
      text += `Short: ${ann.short_description}\n`;
      text += `Summary: ${ann.summary}\n\n`;
    }
  }

  output.push({
    json: {
      page_index: page.index,
      embedding_text: text.trim(),
      images: page.images   // ✅ keep original images array here
    }
  });
}

return output;

// Input: Array of document objects (like in your example)
// Output: Single merged JSON object

const docs = items.map(item => item.json);

// Take metadata from the first document
const merged = {
  "Document name": docs[0]["Document name"],
  "source": docs[0][" source"], // note: key has leading space in input
  "ASSIGNEDTO": docs[0]["ASSIGNEDTO"],
  "PROJECT": docs[0]["PROJECT"],
  "DOCUMENT_TYPE": docs[0]["DOCUMENT_TYPE"],
   "Source_url": docs[0]["source_url"],
    "Last_modified": docs[0]["last_edit"],
  "Document data": docs.map(d => d["Document data"]).join("\n\n---\n\n") // merge all pages with separator
};

return [{ json: merged }];

